import React from "react";

const Performfilter = ({data}) => {
    const filteredData = data.filter((items) => {
        return items.population != null;
    })
    //console.log(filteredData);

    const findMaxRecoveryRatio = (arr) => {
        let max = 0;
        let nation = "";
        for(let i=0; i<arr.length; i++){
            if((parseInt(arr[i].cases.recovered)/parseInt(arr[i].cases.total)) > max){
                max = (parseInt(arr[i].cases.recovered)/parseInt(arr[i].cases.total));
                nation = arr[i].country
            }
        }
        return nation;
        
    }
    const findMinCFR = (arr) => {
        let min = 1;
        let nation = "";
        for(let i = 0; i<arr.length; i++){
            if((parseInt(arr[i].deaths.total)/parseInt(arr[i].cases.total)) < min){
                min = (parseInt(arr[i].deaths.total)/parseInt(arr[i].cases.total));
                nation = arr[i].country;
            }

        }
        return nation,min;
    }
    console.log(findMinCFR(filteredData))
    return(
        <div>
            <h1>{'Best Recovery Ratio'}</h1>
        </div>
    )
}

export default Performfilter;
