import React,{useState} from "react";
import * as FaIcons from "react-icons/fa";
import { Link } from "react-router-dom";
import Data from "./Links.js";
import "./Sidebar.css";
import {IconContext} from 'react-icons';

export default function Sidebar(){
  const [sidebar, setSidebar] = useState(false);
  const showSidebar = () => {
    setSidebar(!sidebar);
  };
  return (
    <>
    <IconContext.Provider value={{color: '#fff'}}>
      <div className="navbar">
        <Link to="#" className="menu-bars">
          <FaIcons.FaBars onClick={showSidebar} />
        </Link>
      </div>
      <nav className={sidebar ? "nav-menu active" : "nav-menu"}>
      	<div className="header">
      		<div className="text"><p>State</p></div>
      		<div className="text"><p>Of</p></div>
      		<div className="text"><p>COVID</p></div>
      	</div>
        <ul className="nav-menu-item">
          <li className="navbar-toggle">
            <Link to="#" className="menu-bars">
              <FaIcons.FaRegWindowClose onClick={showSidebar}/>
            </Link>
          </li>
          {Data.map((item, index) => {
            return (
              <li key={index} className={item.cName}>
                <Link to={item.path} onClick={showSidebar}>{item.title}</Link>
              </li>
            );
          })}
        </ul>
      </nav>
  </IconContext.Provider>
    </>
  );
}
