import React from "react";
import * as FaIcons from "react-icons/fa";
import { Link } from "react-router-dom";
import Data from "./Links.js";
import "./Sidebar.css";
import {IconContext} from 'react-icons';

class Sidebar extends React.Component{
  constructor(){
    super();
    this.state = {
      sidebar: false,
      dataset: []
    };
  };
  componentDidMount() {
    fetch("https://covid-193.p.rapidapi.com/statistics", {
      method: "GET",
      headers: {
        "x-rapidapi-key": "ca3d776725msh24046ff071ebdb9p1ea1aejsnfbea281c6555",
        "x-rapidapi-host": "covid-193.p.rapidapi.com"
      }
    })
      .then((response) => response.json())
      .then((jsondata) => this.setState({ dataset: jsondata.response }))
      .catch((err) => {
        console.error(err);
      });
  }
  showSidebar = () => {
    this.setState({sidebar: (!this.state.sidebar)})
  }
render(){

  const global = this.state.dataset.filter((all) => {
  return all.country.includes("All");
  });

  return (
    <>
    <IconContext.Provider value={{color: '#41c7c7'}}>
      <div className="navbar">
        <Link to="#" className="menu-bars">
          <FaIcons.FaBars onClick={this.showSidebar} />
        </Link>
        <div className="global">
          <div>
            <p> {"Total Cases: "} {global[0].cases.total} </p>
          </div>
        </div>
      </div>
      <nav className={this.state.sidebar ? "nav-menu active" : "nav-menu"}>
      	<div className="header">
      		<div className="text"><p style={{color: "white"}}>State</p></div>
      		<div className="text"><p style={{color: "#41c7c7"}}>Of</p></div>
      		<div className="text"><p style={{color: "wheat"}}>COVID</p></div>
      		<div className="text"><p style={{color: "#fe6a6a"}}>2021</p></div>
      	</div>
    	<div className="navbar-toggle">
            <Link to="#" className="menu-bars">
              <FaIcons.FaRegWindowClose onClick={this.showSidebar}/>
            </Link>
        </div>
        <ul className="nav-menu-item">
          {Data.map((item, index) => {
            return (
              <li key={index} className={item.cName}>
                <Link to={item.path} onClick={this.showSidebar}>{item.title}</Link>
              </li>
            );
          })}
        </ul>
      </nav>
  </IconContext.Provider>
    </>
  );
}
}

export default Sidebar;
