import React from "react";
import {ResponsiveBar} from "@nivo/bar";

const colorScheme = ["nivo", "set3", "pastel1","yellow_orange_red", "accent", "category10"];


const Testsgraph = ({ genius, title}) => {
const pick = Math.floor(Math.random()*colorScheme.length);

const tooltip = (str) => {
    color: str
}

const data3 = genius.slice(0,15);
	return (
		<div style={{height: "60vh", width: "100%"}}>
		<ResponsiveBar
        data={data3}
        keys={title}
        indexBy="country"
        margin={{ top: 50, right: 60, bottom: 50, left: 130 }}
        padding={0.3}
        layout="horizontal"
        valueScale={{ type: 'linear' }}
        indexScale={{ type: 'band', round: true }}
        theme={
            {
            textColor: "#41c7c7",
            fontSize: "14px",
            fontFamily: "IBM Plex Mono"
        }}
        colors={{ scheme: colorScheme[pick] }}
        colorBy="index"
        borderRadius= {1}
        borderWidth={1}
        borderColor={{ from: 'color', modifiers: [ [ 'darker', 1.6 ] ] }}
        axisTop={null}
        axisRight={null}
        axisBottom={{
            tickSize: 5,
            tickPadding: 5,
            tickRotation: 0,
            legend: title,
            legendPosition: 'middle',
            legendOffset: 40,
        }}
        axisLeft={{
            tickSize: 5,
            tickPadding: 5,
            tickRotation: 0
        }}
        enableGridY={false}
        labelSkipWidth={12}
        labelSkipHeight={12}
        tooltip= {tooltip("#333")}
        labelTextColor={{ from: '#333', modifiers: [ [ 'darker', 1.6 ] ] }}
        animate={true}
        motionStiffness={90}
        motionDamping={15}
    />
		</div>
		)
}

export default Testsgraph;
