import React from "react";
import './performers.css';
import LottieAnimation from './Lottie';
import confetti from './confetti.json';

class Performfilter extends React.Component {
    constructor(){
        super();
        this.state = {
            results: false
        }
    }
    showResults = () =>{
        this.setState({results: (!this.state.results)})
    }
    render(){
    const findMaxRecoveryRatio = (arr) => {
        let max = 0;
        let nation = "";
        let solve = [];
        for(let i=0; i<arr.length; i++){
            if((parseInt(arr[i].cases.recovered)/parseInt(arr[i].cases.total)) > max){
                max = (parseInt(arr[i].cases.recovered)/parseInt(arr[i].cases.total));
                nation = arr[i].country
            }
        }
        solve.push(max,nation);
        return solve;
    }
    const findMinCFR = (arr) => {
        let min = 1;
        let nation = "";
        let solve = [];
        for(let i = 0; i<arr.length; i++){
            if((parseInt(arr[i].deaths.total)/parseInt(arr[i].cases.total)) < min){
                min = (parseInt(arr[i].deaths.total)/parseInt(arr[i].cases.total)).toFixed(7);
                nation = arr[i].country;
            }

        }
        solve.push(min,nation);
        return solve;
    }
    const CFR = findMinCFR(this.props.data);
    const RR = findMaxRecoveryRatio(this.props.data);
    return(
        <div>
            <div className="performer_wrapper">
                <h1 className="performer_header_main">{'Best Case Fatality Ratio'}</h1>
                <div className="performer_content">
                    <div className="performer_section" id="wrapper">
                        <button className={this.state.results ? "performer_button dissolve vanish" : "performer_button"} id="button" onClick={this.showResults}>check results</button>
                        <p className={this.state.results ? "performer_text_header" : "performer_text_header vanish"} id="text">{CFR[1]}</p>
                        <p className={this.state.results ? "performer_text" : "performer_text vanish"}>{CFR[1] + " has a Case Fatality Ratio of just " + CFR[0] + "."}</p>
                    </div>
                    <div className={this.state.results ? "confetti" : "hide"}>
                        <LottieAnimation lotti={confetti} height={300} width={300}/>
                    </div>
                </div>
                <h1 className="performer_header_main">{'Best Recovery Ratio'}</h1>
                <div className="performer_content">
                <div className="performer_section" id="wrapper">
                    <button className={this.state.results ? "performer_button dissolve vanish" : "performer_button"} id="button" onClick={this.showResults}>check results</button>
                    <p className={this.state.results ? "performer_text_header" : "performer_text_header vanish"} id="text">{RR[1]}</p>
                    <p className={this.state.results ? "performer_text" : "performer_text vanish"}>{RR[1] + " has the highest Recovery Ratio of " + RR[0] + "."}</p>
                </div>
                <div className={this.state.results ? "confetti" : "hide"}>
                    <LottieAnimation lotti={confetti} height={300} width={300}/>
                </div>
            </div>
            </div> 
        </div>
    )
    }
}

export default Performfilter;
