import React,{useState} from "react";
import './performers.css';
import LottieAnimation from './Lottie';
import confetti from './confetti.json';

const Performfilter = ({data}) => {
    const filteredData = data.filter((items) => {
        return items.population != null;
    })
    //console.log(filteredData);
    const [results,setResults] = useState(false);
    const findMaxRecoveryRatio = (arr) => {
        let max = 0;
        let nation = "";
        for(let i=0; i<arr.length; i++){
            if((parseInt(arr[i].cases.recovered)/parseInt(arr[i].cases.total)) > max){
                max = (parseInt(arr[i].cases.recovered)/parseInt(arr[i].cases.total));
                nation = arr[i].country
            }
        }
        return nation;
        
    }
    const findMinCFR = (arr) => {
        let min = 1;
        let nation = "";
        let cases = 0;
        let deaths = 0;
        let solve = [];
        for(let i = 0; i<arr.length; i++){
            if((parseInt(arr[i].deaths.total)/parseInt(arr[i].cases.total)) < min){
                min = (parseInt(arr[i].deaths.total)/parseInt(arr[i].cases.total)).toFixed(7);
                nation = arr[i].country;
                cases = arr[i].cases.total;
                deaths = arr[i].deaths.total;
            }

        }
        solve.push(min,nation,cases,deaths);
        return solve;
    }
    let CFR = findMinCFR(filteredData);
    const showResults = () =>{
        console.log(results);
    }
    const button = document.querySelector('#button');
    const confetti = document.querySelector('#confetti');
    const textblock = document.querySelector('#text');
    function myfunction(){
        button.classList.add("vanish");
        textblock.classList.remove("vanish");
        confetti.style.display = "flex";
    }
    return(
        <div>
            <div className="performer_wrapper">
            <h1 className="performer_header_main">{'Best Case Fatality Ratio'}</h1>
                <div className="performer_content">
                    <div className="performer_section" id="wrapper">
                        <button className="performer_button" onClick={myfunction()}>check results</button>
                        <p className="performer_text vanish" id="text">{CFR[1]}</p>
                    </div>
                    <div className="performer_section" id="confetti">
                        <LottieAnimation lotti={confetti} height={300} width={300} />
                    </div>
                </div>
            </div> 
        </div>
    )
}

export default Performfilter;
