import React from "react";
import {ResponsiveBar} from "@nivo/bar";



const Testsgraph = ({label, data2}) => {
const data = [
  {
    "country": label[0],
    "tests conducted": data2[0],
    "tests conductedColor": "hsl(204, 70%, 50%)"
  },
  {
    "country": "AE",
    "tests conducted": 169,
    "tests conductedColor": "hsl(204, 30%, 20%)"
  },
  {
    "country": "AF",
    "tests conducted": 200,
    "tests conductedColor": "hsl(204, 70%, 50%)"
  },
  {
    "country": "AG",
    "tests conducted": 250,
    "tests conductedColor": "hsl(44, 70%, 50%)"
  },
  {
    "country": "AI",
    "tests conducted": 48,
    "tests conductedColor": "hsl(104, 70%, 50%)"
  },
  {
    "country": "AL",
    "tests conducted": 99,
    "tests conductedColor": "hsl(84, 70%, 50%)"
  },
  {
    "country": "AM",
    "tests conducted": 135,
    "tests conductedColor": "hsl(144, 70%, 50%)"

  }
];
	return (
		<div style={{height: "500px", width: "100%"}}>
		<ResponsiveBar
        data={data}
        keys={[ 'tests conducted']}
        indexBy="tests conducted"
        margin={{ top: 50, right: 130, bottom: 50, left: 60 }}
        padding={0.3}
        layout="horizontal"
        valueScale={{ type: 'linear' }}
        indexScale={{ type: 'band', round: true }}
        theme={{textColor: "#41c7c7"}}
        colors={{ scheme: 'nivo' }}
        borderColor={{ from: 'color', modifiers: [ [ 'darker', 1.6 ] ] }}
        axisTop={null}
        axisRight={null}
        axisBottom={{
            tickSize: 5,
            tickPadding: 5,
            tickRotation: 0,
            legend: 'tests conducted',
            legendPosition: 'middle',
            legendOffset: 32
        }}
        axisLeft={{
            tickSize: 5,
            tickPadding: 5,
            tickRotation: 0,
            legend: 'country',
            legendPosition: 'middle',
            legendOffset: -40
        }}
        enableGridY={false}
        labelSkipWidth={12}
        labelSkipHeight={12}
        labelTextColor={{ from: 'color', modifiers: [ [ 'darker', 1.6 ] ] }}
        animate={true}
        motionStiffness={90}
        motionDamping={15}
    />
		</div>
		)
}

export default Testsgraph;
