import React from "react";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Text,
  Legend,
  ResponsiveContainer
} from "recharts";
import "./graphs.css";

export default function Nationgraph({data}) {

  const labelArray = Object.keys(data);

  const casesData = Object.values(data);

  const casesAdd = (arr1, arr2) => {
    let dataset = [];
    let Obj = function(v1,v2){
        this.name = v1;
        this.Cases = v2;
    }
    for (let i=arr1.length-1; i>0; i--){
        let obj = new Obj(arr1[i],arr2[i].total_cases);
        dataset.push(obj);
    }
    return dataset;
  }
    const deathsAdd = (arr1, arr2) => {
    let dataset = [];
    let Obj = function(v1,v2){
        this.name = v1;
        this.Cases = v2;
    }
    for (let i=arr1.length-1; i>0;i--){
        let obj = new Obj(arr1[i],arr2[i].deaths);
        dataset.push(obj);
    }
    return dataset;
  }
    const testedAdd = (arr1, arr2) => {
    let dataset = [];
    let Obj = function(v1,v2){
        this.name = v1;
        this.Cases = v2;
    }
    for (let i=arr1.length-1; i>0;i--){
        let obj = new Obj(arr1[i],arr2[i].tested);
        dataset.push(obj);
    }
    return dataset;
  }
    const recratioAdd = (arr1, arr2) => {
    let dataset = [];
    let Obj = function(v1,v2){
        this.name = v1;
        this.Cases = v2;
    }
    for (let i=arr1.length-1; i>0;i--){
        let obj = new Obj(arr1[i],arr2[i].recovery_ratio);
        dataset.push(obj);
    }
    return dataset;
  }
  const dataCases = casesAdd(labelArray,casesData);
  const dataDeaths = deathsAdd(labelArray,casesData);
  const dataTests = testedAdd(labelArray,casesData);
  const dataRR = recratioAdd(labelArray,casesData);
   console.log(casesData);
  return (
    <div className="graph_container">
    <div>
    <ResponsiveContainer width="100%" height={400}>
    <LineChart
      data={dataCases}
      margin={{
        top: 5,
        right: 30,
        left: 20,
        bottom: 5
      }}
    >
      <XAxis dataKey="date" />
      <YAxis dataKey=""Cases/>
      <Text scaleToFit="true"/>
      <Tooltip />
      <Legend />
      <Line
        type="monotone"
        dataKey="Cases"
        stroke="#8884d8"
        strokeWidth="5px"
        dot={{ stroke: '#41caca', strokeWidth: 2 }}
        activeDot={{stroke: "#fe6a6a", strokeWidth: 2, r: 8 }}
      />
    </LineChart></ResponsiveContainer></div>
    <div>
        <ResponsiveContainer width="100%" height={400}>
    <LineChart
      data={dataDeaths}
      margin={{
        top: 5,
        right: 30,
        left: 20,
        bottom: 5
      }}
    >
      <XAxis dataKey="date" />
      <YAxis/>
      <Text scaleToFit="true"/>
      <Tooltip />
      <Legend />
      <Line
        type="monotone"
        dataKey="Cases"
        stroke="#8884d8"
        strokeWidth="5px"
        dot={{ stroke: '#41caca', strokeWidth: 2 }}
        activeDot={{stroke: "#fe6a6a", strokeWidth: 2, r: 8 }}
      />
    </LineChart></ResponsiveContainer></div>
    </div>
  );
}
