import React from "react";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Text,
  Legend,
  ResponsiveContainer
} from "recharts";
import "./graphs.css";

const data2 = [
  {
    name: "Page A",
    uv: 4000,
    pv: 2400,
    amt: 2400
  },
  {
    name: "Page B",
    uv: 3000,
    pv: 1398,
    amt: 2210
  },
  {
    name: "Page C",
    uv: 2000,
    pv: 9800,
    amt: 2290
  },
  {
    name: "Page D",
    uv: 2780,
    pv: 3908,
    amt: 2000
  },
  {
    name: "Page E",
    uv: 1890,
    pv: 4800,
    amt: 2181
  },
  {
    name: "Page F",
    uv: 2390,
    pv: 3800,
    amt: 2500
  },
  {
    name: "Page G",
    uv: 3490,
    pv: 4300,
    amt: 2100
  }
];

export default function Nationgraph({data}) {

  const labelArray = Object.keys(data);

  const casesData = Object.values(data);

  // const casesAdd2 = (arr) => {
  //   let dataset2 = [];
  //   for(let j = 0; j<arr.length-1; j++){
  //     dataset2.push(arr[j].total_cases);
  //   }
  // }
  // const casesData2 = casesAdd2(casesData);

  const casesAdd = (arr1, arr2) => {
    let dataset = [];
    let Obj = function(v1,v2){
        this.name = v1;
        this.Cases = v2;
    }
    for (let i=arr1.length-1; i>0;i--){
        let obj = new Obj(arr1[i],arr2[i].total_cases);
        dataset.push(obj);
    }
    return dataset;
  }
    const deathsAdd = (arr1, arr2) => {
    let dataset = [];
    let Obj = function(v1,v2){
        this.name = v1;
        this.Deaths = v2;
    }
    for (let i=arr1.length-1; i>0;i--){
        let obj = new Obj(arr1[i],arr2[i].deaths);
        dataset.push(obj);
    }
    return dataset;
  }
    const testAdd = (arr1, arr2) => {
    let dataset = [];
    let Obj = function(v1,v2){
        this.name = v1;
        this.Tests = v2;
    }
    for (let i=arr1.length-1; i>0;i--){
        let obj = new Obj(arr1[i],arr2[i].tested);
        dataset.push(obj);
    }
    return dataset;
  }
      const rrAdd = (arr1, arr2) => {
    let dataset = [];
    let Obj = function(v1,v2){
        this.name = v1;
        this.recovery_ratio = v2;
    }
    for (let i=arr1.length-1; i>0;i--){
        let obj = new Obj(arr1[i],arr2[i].recovery_ratio);
        dataset.push(obj);
    }
    return dataset;
  }
  const data3 = casesAdd(labelArray,casesData);

  const data4 = deathsAdd(labelArray,casesData);

  const data5 = testAdd(labelArray,casesData);

  const data6 = rrAdd(labelArray,casesData);

   console.log(data3);
  return (
    <div className="graph_container">
    <div>
    <ResponsiveContainer width="100%" height={400}>
    <LineChart
      data={data3}
      margin={{
        top: 5,
        right: 30,
        left: 20,
        bottom: 5
      }}
    >
      <XAxis dataKey="date" />
      <YAxis dataKey=""Cases/>
      <Text scaleToFit="true"/>
      <Tooltip />
      <Legend />
      <Line
        type="monotone"
        dataKey="Cases"
        stroke="#8884d8"
        strokeWidth="5px"
        dot={{ stroke: '#41caca', strokeWidth: 2 }}
        activeDot={{stroke: "#fe6a6a", strokeWidth: 2, r: 8 }}
      />
    </LineChart></ResponsiveContainer></div>
    <div>
        <ResponsiveContainer width="100%" height={400}>
    <LineChart
      data={data4}
      margin={{
        top: 5,
        right: 30,
        left: 20,
        bottom: 5
      }}
    >
      <XAxis dataKey="date" />
      <YAxis dataKey=""Cases/>
      <Text scaleToFit="true"/>
      <Tooltip />
      <Legend />
      <Line
        type="monotone"
        dataKey="Deaths"
        stroke="var(--alert_red)"
        strokeWidth="5px"
        dot={{ stroke: '#41caca', strokeWidth: 2 }}
        activeDot={{stroke: "#fe6a6a", strokeWidth: 2, r: 8 }}
      />
    </LineChart></ResponsiveContainer></div>
    <div>
        <ResponsiveContainer width="100%" height={400}>
    <LineChart
      data={data5}
      margin={{
        top: 5,
        right: 30,
        left: 20,
        bottom: 5
      }}
    >
      <XAxis dataKey="date" />
      <YAxis dataKey=""Cases/>
      <Text scaleToFit="true"/>
      <Tooltip />
      <Legend />
      <Line
        type="monotone"
        dataKey="Tests"
        stroke="var(--positive_green)"
        strokeWidth="5px"
        dot={{ stroke: '#41caca', strokeWidth: 2 }}
        activeDot={{stroke: "#fe6a6a", strokeWidth: 2, r: 8 }}
      />
    </LineChart></ResponsiveContainer></div>
    <div>
        <ResponsiveContainer width="100%" height={400}>
    <LineChart
      data={data6}
      margin={{
        top: 5,
        right: 30,
        left: 20,
        bottom: 5
      }}
    >
      <XAxis dataKey="date" />
      <YAxis dataKey=""Cases/>
      <Text scaleToFit="true"/>
      <Tooltip />
      <Legend />
      <Line
        type="monotone"
        dataKey="recovery_ratio"
        stroke="var(--vibe_pink)"
        strokeWidth="5px"
        dot={{ stroke: '#41caca', strokeWidth: 2 }}
        activeDot={{stroke: "#fe6a6a", strokeWidth: 2, r: 8 }}
      />
    </LineChart></ResponsiveContainer></div>
    </div>

  );
}
