import React from "react";
import {ResponsiveBar} from "@nivo/bar";



const Testsgraph = ({label, data2, title}) => {
const data = [
  {
    "country": label[0],
    "tests conducted": data2[0],
    "tests conductedColor": "hsl(0.42, 36%, 70%)"
  },
  {
    "country": label[1],
    "tests conducted": data2[1],
    "tests conductedColor": "hsl(204, 30%, 20%)"
  },
  {
    "country": label[2],
    "tests conducted": data2[2],
    "tests conductedColor": "hsl(204, 70%, 50%)"
  },
    {
    "country": label[3],
    "tests conducted": data2[3],
    "tests conductedColor": "hsl(204, 70%, 50%)"
  },
      {
    "country": label[4],
    "tests conducted": data2[4],
    "tests conductedColor": "hsl(204, 70%, 50%)"
  },
      {
    "country": label[5],
    "tests conducted": data2[5],
    "tests conductedColor": "hsl(204, 70%, 50%)"
  },
      {
    "country": label[6],
    "tests conducted": data2[6],
    "tests conductedColor": "hsl(204, 70%, 50%)"
  },
  {
    "country": label[7],
    "tests conducted": data2[7],
    "tests conductedColor": "hsl(204, 70%, 50%)"
  },
    {
    "country": label[8],
    "tests conducted": data2[8],
    "tests conductedColor": "hsl(204, 70%, 50%)"
  },
  {
    "country": label[9],
    "tests conducted": data2[9],
    "tests conductedColor": "hsl(204, 70%, 50%)"
  },
  {
    "country": label[10],
    "tests conducted": data2[10],
    "tests conductedColor": "hsl(204, 70%, 50%)"
  },
  {
    "country": label[11],
    "tests conducted": data2[11],
    "tests conductedColor": "hsl(204, 70%, 50%)"
  },
  {
    "country": label[12],
    "tests conducted": data2[12],
    "tests conductedColor": "hsl(204, 70%, 50%)"
  },
  {
    "country": label[13],
    "tests conducted": data2[13],
    "tests conductedColor": "hsl(204, 70%, 50%)"
  },
  {
    "country": label[14],
    "tests conducted": data2[14],
    "tests conductedColor": "hsl(204, 70%, 50%)"
  },
  {
    "country": label[15],
    "tests conducted": data2[15],
    "tests conductedColor": "hsl(204, 70%, 50%)"
  },
  {
    "country": label[16],
    "tests conducted": data2[16],
    "tests conductedColor": "hsl(204, 70%, 50%)"
  },
  {
    "country": label[17],
    "tests conducted": data2[17],
    "tests conductedColor": "hsl(204, 70%, 50%)"
  },
  {
    "country": label[18],
    "tests conducted": data2[18],
    "tests conductedColor": "hsl(204, 70%, 50%)"
  },
  {
    "country": label[19],
    "tests conducted": data2[19],
    "tests conductedColor": "hsl(204, 70%, 50%)"
  },
];
	return (
		<div style={{height: "500px", minWidth: "320px", padding: "0 1em"}}>
		<ResponsiveBar
        data={data}
        keys={[ 'tests conducted']}
        indexBy="country"
        margin={{ top: 50, right: 130, bottom: 50, left: 60 }}
        padding={0.3}
        layout="horizontal"
        valueScale={{ type: 'linear' }}
        indexScale={{ type: 'band', round: true }}
        theme={{textColor: "#41c7c7"}}
        colors={{ scheme: 'nivo' }}
        borderColor={{ from: 'color', modifiers: [ [ 'darker', 1.6 ] ] }}
        axisTop={null}
        axisRight={null}
        axisBottom={{
            tickSize: 5,
            tickPadding: 5,
            tickRotation: 0,
            legend: title,
            legendPosition: 'middle',
            legendOffset: 32
        }}
        axisLeft={{
            tickSize: 5,
            tickPadding: 5,
            tickRotation: 0,
            legend: 'country',
            legendPosition: 'middle',
            legendOffset: -55
        }}
        enableGridY={false}
        labelSkipWidth={12}
        labelSkipHeight={12}
        labelTextColor={{ from: 'color', modifiers: [ [ 'darker', 1.6 ] ] }}
        animate={true}
        motionStiffness={90}
        motionDamping={15}
    />
		</div>
		)
}

export default Testsgraph;
