import React from "react";
import './App.css';
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Header from './hero/Header';
import Sidebar from './sidebar/Sidebar';
//import Demographics from './Components/Demographics/Demographics';
import Datawrap from './Components/Leaders/Datawrap';
import Loader from './loader/Loader';
import Cases from './Components/Cases/Cases';

class App extends React.Component {
    constructor(){
    super();
    this.state = {
      data: []
    };
  };

  componentDidMount() {
    fetch("https://covid-193.p.rapidapi.com/statistics", {
      method: "GET",
      headers: {
        "x-rapidapi-key": "ca3d776725msh24046ff071ebdb9p1ea1aejsnfbea281c6555",
        "x-rapidapi-host": "covid-193.p.rapidapi.com"
      }
    })
      .then((response) => response.json())
      //.then((jsondata) => console.log(jsondata.response))
      .then((jsondata) => this.setState({ data: jsondata.response }))
      .catch((err) => {
        console.error(err);
      });
  }

  render(){
  return (
    <div className="App">
        <Router>
        <Sidebar />
        <Switch>
          <Route path="/" exact component={Header} />
          <Route path="/demographics" component={Loader} />
          <Route path="/leaders" exact component={Datawrap} />
          <Route path="/cases" exact component={Cases} />
          </Switch>
      </Router>
    </div>
  );
}}

export default App;
